package Examples;

public class StringManipulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "vicente";
		System.out.println("me name is "+name);
		System.out.println("me name have "+name.length()+" letters");
		System.out.println("the first letter of me name is "+name.charAt(0));
		int lastLetter;
		lastLetter= name.length();
		System.out.println("the last letter is "+name.charAt(lastLetter-1));
		

	}

}
